const char UP = '(';

int part1(){
    static int total;
    const int len = strlen(INPUT);
    for (int i=0;i<len;i++){
        if (INPUT[i] == UP) {
            total++;
        } else {
            total--;
        }
    }
    return total;
}

int part2(){
    static int total;
    const int len = strlen(INPUT);
    for (int i=0;i<len;i++){
        if (INPUT[i] == UP) {
            total++;
        } else {
            total--;
        }
        if (total == -1) {
            return i+1;
        }
    }
    return 0;
}

int main()
{
    printf("%d\n",part1());
    printf("%d\n",part2());
    return 0;
}