const char UP = '(';
static int total;
static int len;

int part1(){
    total = 0;
    for (int i=0;i<len;i++){
        if (INPUT[i] == UP) {
            total++;
        } else {
            total--;
        }
    }
    return total;
}

int part2(){
    total = 0;
    for (int i=0;i<len;i++){
        if (INPUT[i] == UP) {
            total++;
        } else {
            total--;
        }
        if (total == -1) {
            return i+1;
        }
    }
    return 0;
}

int main(){
    len = strlen(INPUT);
    printf("%d\n%d\n",part1(),part2());
    return 0;
}