const UP rune = rune('(')

var total int = 0

func part1() int {
	total = 0
	for _, char := range INPUT {
		if char == UP {
			total++
		} else {
			total--
		}
	}
	return total
}

func part2() int {
	total = 0
	for i, char := range INPUT {
		if char == UP {
			total++
		} else {
			total--
		}
		if total == -1 {
			return i + 1
		}
	}
	return 0
}
func main() {
	fmt.Println(part1(), part2())
}
