const UP rune = rune('(')

func part1(input string) int {
	var total int = 0
	for _, char := range input {
		if char == UP {
			total++
		} else {
			total--
		}
	}
	return total
}

func part2(input string) int {
	var total int = 0
	for i, char := range input {
		if char == UP {
			total++
		} else {
			total--
		}
		if total == -1 {
			return i + 1
		}
	}
	return 0
}
func main() {
	fmt.Println(part1(INPUT))
	fmt.Println(part2(INPUT))
}
