def answer1(input)
	result = 0
	input.each_char { |char|
		if char == "("
			result += 1
		else
			result -= 1
		end
	}
	result.to_s
end

def answer2(input)
	result = 0
	floor = 0
	input.each_char { |char|
		result += 1
		if char == "("
			floor += 1
		else
			floor -= 1
		end
		if floor == -1
			return result.to_s
		end
	}
end

puts "the answer 1 is " + answer1(input)
puts "the answer 2 is " + answer2(input)